$(document).ready(function(){$(function(){$('a[href*="#"]:not([href="#"])').click(function(){if(location.pathname.replace(/^\//,"")==this.pathname.replace(/^\//,"")&&location.hostname==this.hostname){var e=$(this.hash);if((e=e.length?e:$("[name="+this.hash.slice(1)+"]")).length)return $("html, body").animate({scrollTop:e.offset().top},1e3,"easeInOutExpo"),!1}})}),$(window).scroll(function(){$(this).scrollTop()>100?$(".scrolltop").fadeIn():$(".scrolltop").fadeOut()}),$(".scrolltop, #logo a").click(function(){return $("html, body").animate({scrollTop:0},1e3,"easeInOutExpo"),!1}),$("#assessment-form").validate({errorElement:"small",errorPlacement:function(e,o){e.addClass("form-text"),e.insertAfter(o)},highlight:function(e){$(e).parents(".form-group").addClass("has-error").removeClass("has-success")},unhighlight:function(e){$(e).parents(".form-group").addClass("has-success").removeClass("has-error")}})});